#include <string>
#include <vector>
#include <algorithm>
#include <numeric>
#include <iostream>
/*
    input:
    (()) = 0
    ()() = 0
    ((( = 3
    (()(()( = 3
    ))((((( = 3
    )())()) = -3
*/
using namespace std;



int floorInstructions(string input) {
    auto count_parenthesis = [] (int sum, char elem) {
        int elemValue = elem == '(' ? 1 : -1;
        return sum + elemValue;
    };
    return accumulate(input.begin(), input.end(), 0, count_parenthesis);
}

int positionBasementCharacter(string input) {
    int position = 0;
    int sum = 0;
    for (char c : input) {
        int elemValue = c == '(' ? 1 : -1;
        sum += elemValue;
        position++;
        if (sum == -1)
            return position;
    }
    return 0;
}



