

floorInstructions :: [Char] -> Int
floorInstructions [] = 0
floorInstructions (x:xs)
    |   x == '(' = floorInstructions xs + 1
    |   x == ')' = floorInstructions xs - 1


positionBasementCharacter :: [Char] -> Int -> Int -> Int
positionBasementCharacter [] _ _ = 0
positionBasementCharacter (x:xs) sumResult indexBasement
    |   sumResult == -1 = indexBasement
    |   x == '(' = positionBasementCharacter xs (sumResult + 1) (indexBasement + 1)
    |   x == ')' = positionBasementCharacter xs (sumResult - 1) (indexBasement + 1)





